contracts/LiquidityPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title LiquidityPool
 * @notice Gerencia pools de liquidez MNEE/Stables com AMM simples
 * @dev Base para integração com Uniswap V3 futura
 */
contract LiquidityPool is ReentrancyGuard, Pausable, Ownable {
    using SafeERC20 for IERC20;

    struct Pool {
        address token0; // Token esquerda (ex: MNEE)
        address token1; // Token direita (ex: USDC)
        uint256 reserve0; // Saldo de token0
        uint256 reserve1; // Saldo de token1
        uint256 totalShares; // LP tokens emitidos
        bool active;
    }

    struct LPPosition {
        uint256 poolId;
        uint256 shares; // Quantidade de LP tokens possuídos
        uint256 depositedToken0;
        uint256 depositedToken1;
    }

    event PoolCreated(
        uint256 indexed poolId,
        address indexed token0,
        address indexed token1,
        uint256 initialLiquidity0,
        uint256 initialLiquidity1
    );

    event LiquidityAdded(
        uint256 indexed poolId,
        address indexed provider,
        uint256 amount0,
        uint256 amount1,
        uint256 sharesIssued
    );

    event LiquidityRemoved(
        uint256 indexed poolId,
        address indexed provider,
        uint256 amount0,
        uint256 amount1,
        uint256 sharesBurned
    );

    event Swapped(
        uint256 indexed poolId,
        address indexed user,
        address indexed tokenIn,
        uint256 amountIn,
        uint256 amountOut,
        uint256 newReserve0,
        uint256 newReserve1
    );

    event PoolFeeCollected(
        uint256 indexed poolId,
        uint256 fee0,
        uint256 fee1
    );

    // State
    uint256 public nextPoolId;
    mapping(uint256 => Pool) public pools;
    mapping(address => mapping(uint256 => LPPosition)) public lpPositions; // user -> poolId -> position
    mapping(uint256 => uint256) public collectedFees0; // poolId -> fee amount
    mapping(uint256 => uint256) public collectedFees1; // poolId -> fee amount

    // Constants
    uint256 public constant FEE_PERCENTAGE = 30; // 0.3% = 30 basis points
    uint256 public constant PRECISION = 10000; // Para cálculos com taxas

    // Minimum liquidity para evitar divisão por zero
    uint256 public constant MINIMUM_LIQUIDITY = 10 ** 3;

    constructor() Ownable() {}

    /**
     * @notice Cria um novo pool de liquidez
     * @param token0 Primeiro token (ex: MNEE)
     * @param token1 Segundo token (ex: USDC)
     * @param amount0 Quantidade inicial de token0
     * @param amount1 Quantidade inicial de token1
     * @return poolId ID do pool criado
     */
    function createPool(
        address token0,
        address token1,
        uint256 amount0,
        uint256 amount1
    ) external nonReentrant whenNotPaused returns (uint256) {
        require(token0 != address(0) && token1 != address(0), "Invalid tokens");
        require(token0 != token1, "Tokens must be different");
        require(amount0 > 0 && amount1 > 0, "Amounts must be > 0");

        // Ordenar tokens por endereço (padrão Uniswap)
        (address _token0, address _token1, uint256 _amount0, uint256 _amount1) = 
            token0 < token1 
            ? (token0, token1, amount0, amount1)
            : (token1, token0, amount1, amount0);

        uint256 poolId = nextPoolId;

        // Calcular LP shares iniciais (geometria média)
        uint256 liquidity = _sqrt(_amount0 * _amount1);
        require(liquidity > MINIMUM_LIQUIDITY, "Insufficient liquidity");

        // Queimar MINIMUM_LIQUIDITY para evitar divisão por zero
        uint256 sharesIssued = liquidity - MINIMUM_LIQUIDITY;

        // Transferir tokens
        IERC20(_token0).safeTransferFrom(msg.sender, address(this), _amount0);
        IERC20(_token1).safeTransferFrom(msg.sender, address(this), _amount1);

        // Criar pool
        pools[poolId] = Pool({
            token0: _token0,
            token1: _token1,
            reserve0: _amount0,
            reserve1: _amount1,
            totalShares: liquidity,
            active: true
        });

        // Registrar LP position
        lpPositions[msg.sender][poolId] = LPPosition({
            poolId: poolId,
            shares: sharesIssued,
            depositedToken0: _amount0,
            depositedToken1: _amount1
        });

        emit PoolCreated(poolId, _token0, _token1, _amount0, _amount1);
        nextPoolId++;
        return poolId;
    }

    /**
     * @notice Adiciona liquidez a um pool existente
     * @param poolId ID do pool
     * @param amount0 Quantidade de token0 a depositar
     * @param amount1 Quantidade de token1 a depositar
     */
    function addLiquidity(
        uint256 poolId,
        uint256 amount0,
        uint256 amount1
    ) external nonReentrant whenNotPaused {
        require(_poolExists(poolId), "Pool does not exist");
        Pool storage pool = pools[poolId];
        require(pool.active, "Pool is inactive");
        require(amount0 > 0 && amount1 > 0, "Amounts must be > 0");

        // Calcular shares baseado na razão de liquidez
        uint256 shares = _min(
            (amount0 * pool.totalShares) / (pool.reserve0 + 1), // +1 para evitar divisão por zero
            (amount1 * pool.totalShares) / (pool.reserve1 + 1)
        );

        require(shares > 0, "Insufficient liquidity provided");

        // Transferir tokens
        IERC20(pool.token0).safeTransferFrom(msg.sender, address(this), amount0);
        IERC20(pool.token1).safeTransferFrom(msg.sender, address(this), amount1);

        // Atualizar pool
        pool.reserve0 += amount0;
        pool.reserve1 += amount1;
        pool.totalShares += shares;

        // Registrar ou atualizar LP position
        lpPositions[msg.sender][poolId].shares += shares;
        lpPositions[msg.sender][poolId].depositedToken0 += amount0;
        lpPositions[msg.sender][poolId].depositedToken1 += amount1;

        emit LiquidityAdded(poolId, msg.sender, amount0, amount1, shares);
    }

    /**
     * @notice Remove liquidez de um pool
     * @param poolId ID do pool
     * @param shares Quantidade de LP shares a queimar
     */
    function removeLiquidity(
        uint256 poolId,
        uint256 shares
    ) external nonReentrant {
        require(_poolExists(poolId), "Pool does not exist");
        Pool storage pool = pools[poolId];
        require(pool.totalShares > 0, "Invalid pool state");

        LPPosition storage position = lpPositions[msg.sender][poolId];
        require(position.shares >= shares, "Insufficient shares");

        // Calcular amounts a retornar
        uint256 amount0 = (shares * pool.reserve0) / pool.totalShares;
        uint256 amount1 = (shares * pool.reserve1) / pool.totalShares;

        require(amount0 > 0 && amount1 > 0, "Amounts must be > 0");

        // Atualizar state
        position.shares -= shares;
        pool.reserve0 -= amount0;
        pool.reserve1 -= amount1;
        pool.totalShares -= shares;

        // Transferir tokens de volta
        IERC20(pool.token0).safeTransfer(msg.sender, amount0);
        IERC20(pool.token1).safeTransfer(msg.sender, amount1);

        emit LiquidityRemoved(poolId, msg.sender, amount0, amount1, shares);
    }

    /**
     * @notice Faz swap de token0 por token1 (ou vice-versa)
     * @param poolId ID do pool
     * @param tokenIn Endereço do token a ser enviado
     * @param amountIn Quantidade do token de entrada
     * @param minAmountOut Proteção de slippage (mínimo a receber)
     * @return amountOut Quantidade do token recebido
     */
    function swap(
        uint256 poolId,
        address tokenIn,
        uint256 amountIn,
        uint256 minAmountOut
    ) external nonReentrant whenNotPaused returns (uint256 amountOut) {
        require(_poolExists(poolId), "Pool does not exist");
        Pool storage pool = pools[poolId];
        require(pool.active, "Pool is inactive");
        require(amountIn > 0, "Amount must be > 0");
        require(tokenIn == pool.token0 || tokenIn == pool.token1, "Invalid token");

        // Determinar direção do swap
        bool isToken0To1 = tokenIn == pool.token0;
        uint256 reserveIn = isToken0To1 ? pool.reserve0 : pool.reserve1;
        uint256 reserveOut = isToken0To1 ? pool.reserve1 : pool.reserve0;
        address tokenOut = isToken0To1 ? pool.token1 : pool.token0;

        require(reserveIn > 0 && reserveOut > 0, "Insufficient liquidity");

        // Transferir token de entrada
        IERC20(tokenIn).safeTransferFrom(msg.sender, address(this), amountIn);

        // Calcular taxa (0.3%)
        uint256 fee = (amountIn * FEE_PERCENTAGE) / PRECISION;
        uint256 amountInAfterFee = amountIn - fee;

        // Fórmula AMM: x*y = k (constant product)
        uint256 numerator = amountInAfterFee * reserveOut;
        uint256 denominator = reserveIn + amountInAfterFee;
        amountOut = numerator / denominator;

        require(amountOut >= minAmountOut, "Excessive slippage");
        require(amountOut <= reserveOut, "Insufficient output liquidity");

        // Transferir token de saída
        IERC20(tokenOut).safeTransfer(msg.sender, amountOut);

        // Atualizar reserves
        if (isToken0To1) {
            pool.reserve0 += amountIn;
            pool.reserve1 -= amountOut;
            collectedFees0[poolId] += fee;
        } else {
            pool.reserve1 += amountIn;
            pool.reserve0 -= amountOut;
            collectedFees1[poolId] += fee;
        }

        emit Swapped(poolId, msg.sender, tokenIn, amountIn, amountOut, pool.reserve0, pool.reserve1);

        return amountOut;
    }

    /**
     * @notice Coleta taxas acumuladas do pool (apenas owner)
     * @param poolId ID do pool
     */
    function collectFees(uint256 poolId) external nonReentrant onlyOwner {
        require(_poolExists(poolId), "Pool does not exist");

        uint256 fee0 = collectedFees0[poolId];
        uint256 fee1 = collectedFees1[poolId];

        require(fee0 > 0 || fee1 > 0, "No fees to collect");

        Pool storage pool = pools[poolId];

        if (fee0 > 0) {
            collectedFees0[poolId] = 0;
            IERC20(pool.token0).safeTransfer(owner(), fee0);
        }

        if (fee1 > 0) {
            collectedFees1[poolId] = 0;
            IERC20(pool.token1).safeTransfer(owner(), fee1);
        }

        emit PoolFeeCollected(poolId, fee0, fee1);
    }

    // ===== VIEW FUNCTIONS =====

    /**
     * @notice Retorna informações de um pool
     */
    function getPoolInfo(uint256 poolId) external view returns (
        address token0,
        address token1,
        uint256 reserve0,
        uint256 reserve1,
        uint256 totalShares,
        bool active
    ) {
        require(_poolExists(poolId), "Pool does not exist");
        Pool storage pool = pools[poolId];
        return (pool.token0, pool.token1, pool.reserve0, pool.reserve1, pool.totalShares, pool.active);
    }

    /**
     * @notice Simula um swap sem executá-lo
     */
    function getSwapAmount(
        uint256 poolId,
        address tokenIn,
        uint256 amountIn
    ) external view returns (uint256 amountOut) {
        require(_poolExists(poolId), "Pool does not exist");
        Pool storage pool = pools[poolId];
        require(tokenIn == pool.token0 || tokenIn == pool.token1, "Invalid token");

        bool isToken0To1 = tokenIn == pool.token0;
        uint256 reserveIn = isToken0To1 ? pool.reserve0 : pool.reserve1;
        uint256 reserveOut = isToken0To1 ? pool.reserve1 : pool.reserve0;

        if (reserveIn == 0 || reserveOut == 0) return 0;

        uint256 fee = (amountIn * FEE_PERCENTAGE) / PRECISION;
        uint256 amountInAfterFee = amountIn - fee;

        uint256 numerator = amountInAfterFee * reserveOut;
        uint256 denominator = reserveIn + amountInAfterFee;

        return numerator / denominator;
    }

    /**
     * @notice Retorna LP position do usuário
     */
    function getLPPosition(address user, uint256 poolId) external view returns (
        uint256 shares,
        uint256 depositedToken0,
        uint256 depositedToken1
    ) {
        LPPosition storage position = lpPositions[user][poolId];
        return (position.shares, position.depositedToken0, position.depositedToken1);
    }

    // ===== INTERNAL FUNCTIONS =====

    function _poolExists(uint256 poolId) internal view returns (bool) {
        return poolId < nextPoolId;
    }

    function _sqrt(uint256 y) internal pure returns (uint256 z) {
        if (y > 3) {
            z = y;
            uint256 x = y / 2 + 1;
            while (x < z) {
                z = x;
                x = (y / x + x) / 2;
            }
        } else if (y != 0) {
            z = 1;
        }
    }

    function _min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a < b ? a : b;
    }

    // ===== EMERGENCY FUNCTIONS =====

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/PoolManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

// Uniswap V3 interfaces
interface INonfungiblePositionManager {
    struct MintParams {
        address token0;
        address token1;
        uint24 fee;
        int24 tickLower;
        int24 tickUpper;
        uint256 amount0Desired;
        uint256 amount1Desired;
        uint256 amount0Min;
        uint256 amount1Min;
        address recipient;
        uint256 deadline;
    }

    struct IncreaseLiquidityParams {
        uint256 tokenId;
        uint256 amount0Desired;
        uint256 amount1Desired;
        uint256 amount0Min;
        uint256 amount1Min;
        uint256 deadline;
    }

    struct DecreaseLiquidityParams {
        uint256 tokenId;
        uint128 liquidity;
        uint256 amount0Min;
        uint256 amount1Min;
        uint256 deadline;
    }

    function mint(MintParams calldata params)
        external
        payable
        returns (uint256 tokenId, uint128 liquidity, uint256 amount0, uint256 amount1);

    function increaseLiquidity(IncreaseLiquidityParams calldata params)
        external
        payable
        returns (uint128 liquidity, uint256 amount0, uint256 amount1);

    function decreaseLiquidity(DecreaseLiquidityParams calldata params)
        external
        payable
        returns (uint256 amount0, uint256 amount1);

    function collect(CollectParams calldata params)
        external
        payable
        returns (uint256 amount0, uint256 amount1);

    struct CollectParams {
        uint256 tokenId;
        address recipient;
        uint128 amount0Max;
        uint128 amount1Max;
    }

    function ownerOf(uint256 tokenId) external view returns (address owner);
    function positions(uint256 tokenId) external view returns (
        uint96 nonce,
        address operator,
        address token0,
        address token1,
        uint24 fee,
        int24 tickLower,
        int24 tickUpper,
        uint128 liquidity,
        uint256 feeGrowthInside0LastX128,
        uint256 feeGrowthInside1LastX128,
        uint128 tokensOwed0,
        uint128 tokensOwed1
    );
}

interface IUniswapV3Factory {
    function getPool(address tokenA, address tokenB, uint24 fee)
        external
        view
        returns (address pool);

    function createPool(address tokenA, address tokenB, uint24 fee)
        external
        returns (address pool);
}

interface IUniswapV3Pool {
    function slot0() external view returns (
        uint160 sqrtPriceX96,
        int24 tick,
        uint16 observationIndex,
        uint16 observationCardinality,
        uint16 observationCardinalityNext,
        uint8 feeProtocol,
        bool unlocked
    );

    function liquidity() external view returns (uint128);
}

/**
 * @title PoolManager
 * @notice Gerencia pools de liquidez Uniswap V3 para StreamPay
 * @dev Permite criar, adicionar e remover liquidez automaticamente
 */
contract PoolManager is ReentrancyGuard, Pausable, Ownable {
    using SafeERC20 for IERC20;

    struct LPRecord {
        uint256 positionId; // NFT token ID do Uniswap V3
        address token0;
        address token1;
        uint24 fee;
        int24 tickLower;
        int24 tickUpper;
        uint256 depositedAmount0;
        uint256 depositedAmount1;
        bool active;
    }

    event PositionCreated(
        uint256 indexed recordId,
        uint256 indexed positionId,
        address indexed token0,
        address token1,
        int24 tickLower,
        int24 tickUpper,
        uint256 amount0,
        uint256 amount1
    );

    event LiquidityIncreased(
        uint256 indexed recordId,
        uint128 liquidity,
        uint256 amount0,
        uint256 amount1
    );

    event LiquidityDecreased(
        uint256 indexed recordId,
        uint256 amount0,
        uint256 amount1
    );

    event FeesCollected(
        uint256 indexed recordId,
        uint256 fee0,
        uint256 fee1
    );

    // State
    uint256 public nextRecordId;
    INonfungiblePositionManager public positionManager;
    IUniswapV3Factory public factory;

    mapping(uint256 => LPRecord) public positions;
    mapping(address => uint256[]) public userPositions; // user -> array of recordIds

    // Default Uniswap V3 fee tiers
    uint24 public constant FEE_TIER_LOW = 500;     // 0.05%
    uint24 public constant FEE_TIER_MEDIUM = 3000; // 0.30%
    uint24 public constant FEE_TIER_HIGH = 10000;  // 1.00%

    constructor(address _positionManager, address _factory) Ownable() {
        require(_positionManager != address(0), "Invalid position manager");
        require(_factory != address(0), "Invalid factory");
        positionManager = INonfungiblePositionManager(_positionManager);
        factory = IUniswapV3Factory(_factory);
    }

    /**
     * @notice Cria uma nova posição de liquidez Uniswap V3
     * @param token0 Primeiro token
     * @param token1 Segundo token
     * @param fee Taxa do pool (500, 3000, 10000)
     * @param amount0Desired Quantidade desejada de token0
     * @param amount1Desired Quantidade desejada de token1
     * @param tickLower Tick inferior da faixa
     * @param tickUpper Tick superior da faixa
     * @return recordId ID do record criado
     */
    function createPosition(
        address token0,
        address token1,
        uint24 fee,
        uint256 amount0Desired,
        uint256 amount1Desired,
        int24 tickLower,
        int24 tickUpper
    ) external nonReentrant whenNotPaused returns (uint256 recordId) {
        require(token0 != address(0) && token1 != address(0), "Invalid tokens");
        require(token0 != token1, "Tokens must be different");
        require(amount0Desired > 0 && amount1Desired > 0, "Amounts must be > 0");
        require(_isValidFeeTier(fee), "Invalid fee tier");
        require(tickLower < tickUpper, "Invalid tick range");

        // Garantir que tokens estão em ordem
        if (token0 > token1) {
            (token0, token1) = (token1, token0);
            (amount0Desired, amount1Desired) = (amount1Desired, amount0Desired);
        }

        // Garantir que pool existe
        address pool = factory.getPool(token0, token1, fee);
        if (pool == address(0)) {
            pool = factory.createPool(token0, token1, fee);
            require(pool != address(0), "Pool creation failed");
        }

        // Transferir tokens do usuário para este contrato
        IERC20(token0).safeTransferFrom(msg.sender, address(this), amount0Desired);
        IERC20(token1).safeTransferFrom(msg.sender, address(this), amount1Desired);

        // Aprovar position manager
        IERC20(token0).forceApprove(address(positionManager), amount0Desired);
        IERC20(token1).forceApprove(address(positionManager), amount1Desired);

        // Chamar mint
        INonfungiblePositionManager.MintParams memory params = 
            INonfungiblePositionManager.MintParams({
                token0: token0,
                token1: token1,
                fee: fee,
                tickLower: tickLower,
                tickUpper: tickUpper,
                amount0Desired: amount0Desired,
                amount1Desired: amount1Desired,
                amount0Min: 0,
                amount1Min: 0,
                recipient: address(this),
                deadline: block.timestamp + 1 hours
            });

        (uint256 tokenId, , uint256 amount0, uint256 amount1) = 
            positionManager.mint(params);

        // Devolver tokens não utilizados
        if (amount0 < amount0Desired) {
            IERC20(token0).safeTransfer(msg.sender, amount0Desired - amount0);
        }
        if (amount1 < amount1Desired) {
            IERC20(token1).safeTransfer(msg.sender, amount1Desired - amount1);
        }

        // Registrar position
        recordId = nextRecordId;
        positions[recordId] = LPRecord({
            positionId: tokenId,
            token0: token0,
            token1: token1,
            fee: fee,
            tickLower: tickLower,
            tickUpper: tickUpper,
            depositedAmount0: amount0,
            depositedAmount1: amount1,
            active: true
        });

        userPositions[msg.sender].push(recordId);

        emit PositionCreated(
            recordId,
            tokenId,
            token0,
            token1,
            tickLower,
            tickUpper,
            amount0,
            amount1
        );

        nextRecordId++;
        return recordId;
    }

    /**
     * @notice Aumenta liquidez em uma posição existente
     * @param recordId ID do record
     * @param amount0Desired Quantidade desejada de token0
     * @param amount1Desired Quantidade desejada de token1
     */
    function increaseLiquidity(
        uint256 recordId,
        uint256 amount0Desired,
        uint256 amount1Desired
    ) external nonReentrant whenNotPaused {
        require(_recordExists(recordId), "Record does not exist");
        LPRecord storage record = positions[recordId];
        require(record.active, "Record is inactive");
        require(amount0Desired > 0 && amount1Desired > 0, "Amounts must be > 0");

        // Transferir tokens
        IERC20(record.token0).safeTransferFrom(msg.sender, address(this), amount0Desired);
        IERC20(record.token1).safeTransferFrom(msg.sender, address(this), amount1Desired);

        // Aprovar
        IERC20(record.token0).forceApprove(address(positionManager), amount0Desired);
        IERC20(record.token1).forceApprove(address(positionManager), amount1Desired);

        // Aumentar liquidez
        INonfungiblePositionManager.IncreaseLiquidityParams memory params =
            INonfungiblePositionManager.IncreaseLiquidityParams({
                tokenId: record.positionId,
                amount0Desired: amount0Desired,
                amount1Desired: amount1Desired,
                amount0Min: 0,
                amount1Min: 0,
                deadline: block.timestamp + 1 hours
            });

        (uint128 liquidity, uint256 amount0, uint256 amount1) =
            positionManager.increaseLiquidity(params);

        // Devolver excedentes
        if (amount0 < amount0Desired) {
            IERC20(record.token0).safeTransfer(msg.sender, amount0Desired - amount0);
        }
        if (amount1 < amount1Desired) {
            IERC20(record.token1).safeTransfer(msg.sender, amount1Desired - amount1);
        }

        record.depositedAmount0 += amount0;
        record.depositedAmount1 += amount1;

        emit LiquidityIncreased(recordId, liquidity, amount0, amount1);
    }

    /**
     * @notice Remove liquidez de uma posição
     * @param recordId ID do record
     * @param liquidity Quantidade de liquidez a remover
     */
    function decreaseLiquidity(
        uint256 recordId,
        uint128 liquidity
    ) external nonReentrant {
        require(_recordExists(recordId), "Record does not exist");
        require(msg.sender == owner(), "Only owner can decrease"); // Simplificado para demo
        LPRecord storage record = positions[recordId];
        require(record.active, "Record is inactive");
        require(liquidity > 0, "Liquidity must be > 0");

        // Diminuir liquidez
        INonfungiblePositionManager.DecreaseLiquidityParams memory params =
            INonfungiblePositionManager.DecreaseLiquidityParams({
                tokenId: record.positionId,
                liquidity: liquidity,
                amount0Min: 0,
                amount1Min: 0,
                deadline: block.timestamp + 1 hours
            });

        (uint256 amount0, uint256 amount1) =
            positionManager.decreaseLiquidity(params);

        // Coletar tokens
        INonfungiblePositionManager.CollectParams memory collectParams =
            INonfungiblePositionManager.CollectParams({
                tokenId: record.positionId,
                recipient: msg.sender,
                amount0Max: type(uint128).max,
                amount1Max: type(uint128).max
            });

        positionManager.collect(collectParams);

        emit LiquidityDecreased(recordId, amount0, amount1);
    }

    /**
     * @notice Coleta fees de uma posição
     * @param recordId ID do record
     */
    function collectFees(uint256 recordId) external nonReentrant {
        require(_recordExists(recordId), "Record does not exist");
        require(msg.sender == owner(), "Only owner can collect fees");
        LPRecord storage record = positions[recordId];

        INonfungiblePositionManager.CollectParams memory params =
            INonfungiblePositionManager.CollectParams({
                tokenId: record.positionId,
                recipient: msg.sender,
                amount0Max: type(uint128).max,
                amount1Max: type(uint128).max
            });

        (uint256 fee0, uint256 fee1) = positionManager.collect(params);

        emit FeesCollected(recordId, fee0, fee1);
    }

    // ===== VIEW FUNCTIONS =====

    /**
     * @notice Retorna informações de uma posição
     */
    function getPositionInfo(uint256 recordId) external view returns (
        uint256 positionId,
        address token0,
        address token1,
        uint24 fee,
        int24 tickLower,
        int24 tickUpper,
        uint256 depositedAmount0,
        uint256 depositedAmount1,
        bool active
    ) {
        require(_recordExists(recordId), "Record does not exist");
        LPRecord storage record = positions[recordId];
        return (
            record.positionId,
            record.token0,
            record.token1,
            record.fee,
            record.tickLower,
            record.tickUpper,
            record.depositedAmount0,
            record.depositedAmount1,
            record.active
        );
    }

    /**
     * @notice Retorna todas as posições do usuário
     */
    function getUserPositions(address user) external view returns (uint256[] memory) {
        return userPositions[user];
    }

    /**
     * @notice Obtém liquidity atual da posição (do Uniswap V3)
     */
    function getPositionLiquidity(uint256 recordId) external view returns (uint128 liquidity) {
        require(_recordExists(recordId), "Record does not exist");
        LPRecord storage record = positions[recordId];

        (, , , , , , , liquidity, , , , ) = positionManager.positions(record.positionId);
        return liquidity;
    }

    /**
     * @notice Verifica se fee tier é válido
     */
    function _isValidFeeTier(uint24 fee) internal pure returns (bool) {
        return fee == FEE_TIER_LOW || fee == FEE_TIER_MEDIUM || fee == FEE_TIER_HIGH;
    }

    /**
     * @notice Verifica se record existe
     */
    function _recordExists(uint256 recordId) internal view returns (bool) {
        return recordId < nextRecordId;
    }

    // ===== EMERGENCY FUNCTIONS =====

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/StreamPayCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title StreamPayCore
 * @notice Contrato de pagamentos streaming ERC20
 * @dev Otimizado para eficiência de gas e integração ElizaOS
 * @dev Versão corrigida com proteções contra cancelamento malicioso
 */
contract StreamPayCore is ReentrancyGuard, Pausable, Ownable {
    using SafeERC20 for IERC20;

    struct Stream {
        address sender;
        address recipient;
        address token;
        uint256 deposit;
        uint256 ratePerSecond;
        uint256 startTime;
        uint256 stopTime;
        uint256 remainingBalance;
        bool active;
    }

    event StreamCreated(
        uint256 indexed streamId,
        address indexed sender,
        address indexed recipient,
        address token,
        uint256 ratePerSecond,
        uint256 duration
    );
    event StreamClaimed(
        uint256 indexed streamId,
        address indexed recipient,
        uint256 amount
    );
    event StreamCancelled(
        uint256 indexed streamId,
        address indexed sender,
        uint256 senderRefund,
        uint256 recipientPayout
    );
    event StreamWithdrawn(
        uint256 indexed streamId,
        address indexed sender,
        uint256 amount
    );

    uint256 public nextStreamId;
    mapping(uint256 => Stream) public streams;

    constructor() Ownable() {}

    /**
     * @notice Cria um novo stream de pagamento
     * @param recipient Endereço do destinatário
     * @param token Endereço do token ERC20
     * @param deposit Quantidade total de tokens a depositar
     * @param ratePerSecond Taxa de tokens por segundo
     * @param duration Duração do stream em segundos
     * @return streamId ID do stream criado
     */
    function createStream(
        address recipient,
        address token,
        uint256 deposit,
        uint256 ratePerSecond,
        uint256 duration
    ) external nonReentrant whenNotPaused returns (uint256) {
        require(recipient != address(0), "Invalid recipient");
        require(recipient != msg.sender, "Cannot stream to self");
        require(token != address(0), "Invalid token");
        require(deposit > 0, "Deposit must be > 0");
        require(ratePerSecond > 0, "Rate must be > 0");
        require(duration > 0, "Duration must be > 0");

        uint256 stopTime = block.timestamp + duration;

        // Validação flexível: deposit deve ser >= ao mínimo necessário
        uint256 minDeposit = ratePerSecond * duration;
        require(deposit >= minDeposit, "Insufficient deposit");

        // Transfere tokens do sender para o contrato
        IERC20(token).safeTransferFrom(msg.sender, address(this), deposit);

        streams[nextStreamId] = Stream({
            sender: msg.sender,
            recipient: recipient,
            token: token,
            deposit: deposit,
            ratePerSecond: ratePerSecond,
            startTime: block.timestamp,
            stopTime: stopTime,
            remainingBalance: deposit,
            active: true
        });

        emit StreamCreated(
            nextStreamId,
            msg.sender,
            recipient,
            token,
            ratePerSecond,
            duration
        );

        nextStreamId++;
        return nextStreamId - 1;
    }

    /**
     * @notice Permite ao destinatário reivindicar tokens acumulados
     * @param streamId ID do stream
     */
    function claim(uint256 streamId) external nonReentrant {
        Stream storage stream = streams[streamId];
        require(stream.active, "Inactive stream");
        require(msg.sender == stream.recipient, "Only recipient can claim");

        uint256 claimable = _calculateClaimable(stream);
        require(claimable > 0, "Nothing to claim");
        require(claimable <= stream.remainingBalance, "Insufficient balance");

        stream.remainingBalance -= claimable;
        IERC20(stream.token).safeTransfer(stream.recipient, claimable);

        emit StreamClaimed(streamId, stream.recipient, claimable);

        // Finaliza stream se todo saldo foi reivindicado ou tempo expirou
        if (stream.remainingBalance == 0 || block.timestamp >= stream.stopTime) {
            stream.active = false;
        }
    }

    /**
     * @notice Consulta saldo disponível para claim
     * @param streamId ID do stream
     * @return Quantidade de tokens disponíveis para reivindicação
     */
    function availableToClaim(uint256 streamId) external view returns (uint256) {
        Stream storage stream = streams[streamId];
        if (!stream.active) return 0;

        uint256 claimable = _calculateClaimable(stream);
        return claimable <= stream.remainingBalance ? claimable : stream.remainingBalance;
    }

    /**
     * @notice Cancela stream, paga ao recipient o que ele já ganhou e devolve o resto ao sender
     * @param streamId ID do stream
     * @dev CORREÇÃO CRÍTICA: Agora protege os tokens já "earned" pelo recipient
     */
    function cancelStream(uint256 streamId) external nonReentrant {
        Stream storage stream = streams[streamId];
        require(stream.active, "Inactive stream");
        require(msg.sender == stream.sender, "Only sender can cancel");

        // Calcular quanto o recipient já tem direito (earned mas não claimed)
        uint256 claimable = _calculateClaimable(stream);

        stream.active = false;

        uint256 recipientPayout = 0;
        uint256 senderRefund = 0;

        // Transferir para recipient o que ele tem direito
        if (claimable > 0 && claimable <= stream.remainingBalance) {
            recipientPayout = claimable;
            IERC20(stream.token).safeTransfer(stream.recipient, recipientPayout);
        }

        // Devolver ao sender apenas o saldo não earned
        uint256 remaining = stream.remainingBalance - recipientPayout;
        if (remaining > 0) {
            senderRefund = remaining;
            IERC20(stream.token).safeTransfer(stream.sender, senderRefund);
        }

        stream.remainingBalance = 0;

        emit StreamCancelled(streamId, stream.sender, senderRefund, recipientPayout);
    }

    /**
     * @notice Permite ao sender retirar fundos de streams expirados e inativos
     * @param streamId ID do stream
     * @dev Útil quando o stream terminou mas o recipient nunca reivindicou
     */
    function withdrawExpiredStream(uint256 streamId) external nonReentrant {
        Stream storage stream = streams[streamId];
        require(!stream.active, "Stream still active");
        require(block.timestamp >= stream.stopTime, "Stream not expired");
        require(msg.sender == stream.sender, "Only sender can withdraw");
        require(stream.remainingBalance > 0, "Nothing to withdraw");

        // Calcular se ainda há tokens não earned pelo recipient
        uint256 elapsed = stream.stopTime - stream.startTime;
        uint256 totalEarned = stream.ratePerSecond * elapsed;
        uint256 alreadyClaimed = stream.deposit - stream.remainingBalance;

        // O recipient teve todo o tempo para reivindicar, então só devolve o excesso
        uint256 unclaimedEarned = totalEarned > alreadyClaimed
        ? totalEarned - alreadyClaimed
        : 0;

        uint256 withdrawable = stream.remainingBalance > unclaimedEarned
        ? stream.remainingBalance - unclaimedEarned
        : 0;

        require(withdrawable > 0, "No withdrawable funds");

        stream.remainingBalance -= withdrawable;
        IERC20(stream.token).safeTransfer(stream.sender, withdrawable);

        emit StreamWithdrawn(streamId, stream.sender, withdrawable);
    }

    /**
     * @notice Função de emergência para pausar criação de novos streams
     * @dev Apenas owner pode pausar/despausar
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Despausa o contrato
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Calcula tokens disponíveis para claim (interno)
     * @param stream Struct do stream
     * @return Quantidade de tokens claimable
     */
    function _calculateClaimable(Stream storage stream) internal view returns (uint256) {
        uint256 elapsed = block.timestamp > stream.stopTime
        ? stream.stopTime - stream.startTime
        : block.timestamp - stream.startTime;

        uint256 totalClaimable = stream.ratePerSecond * elapsed;
        uint256 alreadyClaimed = stream.deposit - stream.remainingBalance;

        uint256 claimable = totalClaimable > alreadyClaimed
        ? totalClaimable - alreadyClaimed
        : 0;

        return claimable;
    }

    /**
     * @notice Retorna informações completas de um stream
     * @param streamId ID do stream
     */
    function getStreamInfo(uint256 streamId) external view returns (
        address sender,
        address recipient,
        address token,
        uint256 deposit,
        uint256 ratePerSecond,
        uint256 startTime,
        uint256 stopTime,
        uint256 remainingBalance,
        bool active,
        uint256 claimableNow
    ) {
        Stream storage stream = streams[streamId];
        return (
            stream.sender,
            stream.recipient,
            stream.token,
            stream.deposit,
            stream.ratePerSecond,
            stream.startTime,
            stream.stopTime,
            stream.remainingBalance,
            stream.active,
            stream.active ? _calculateClaimable(stream) : 0
        );
    }
}

contracts/SwapRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

// Uniswap V3 Swap Router interface
interface ISwapRouter {
    struct ExactInputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 deadline;
        uint256 amountIn;
        uint256 amountOutMinimum;
        uint160 sqrtPriceLimitX96;
    }

    function exactInputSingle(ExactInputSingleParams calldata params)
        external
        payable
        returns (uint256 amountOut);

    struct ExactInputParams {
        bytes path;
        address recipient;
        uint256 deadline;
        uint256 amountIn;
        uint256 amountOutMinimum;
    }

    function exactInput(ExactInputParams calldata params)
        external
        payable
        returns (uint256 amountOut);
}

// Chainlink Price Feed interface para proteção de slippage
interface AggregatorV3Interface {
    function latestRoundData()
        external
        view
        returns (
            uint80 roundId,
            int256 answer,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        );

    function decimals() external view returns (uint8);
}

/**
 * @title SwapRouter
 * @notice Router otimizado para swaps via Uniswap V3 com proteção de slippage via Chainlink
 * @dev Implementa circuit breaker para preços anômalos e suporta multi-hop swaps
 */
contract SwapRouter is ReentrancyGuard, Pausable, Ownable {
    using SafeERC20 for IERC20;

    struct SwapRoute {
        address[] tokens; // Tokens na rota (ex: [MNEE, USDC, USDT])
        uint24[] fees; // Fees para cada hop (ex: [3000, 3000])
        bool active;
    }

    struct OracleConfig {
        address priceFeed; // Chainlink price feed
        uint8 decimals;
        uint256 stalePriceThreshold; // Tempo máximo de dados antigos (segundos)
        int256 lowerBoundPercent; // Limite inferior de preço aceito (em basis points, ex: 9500 = 95%)
        int256 upperBoundPercent; // Limite superior de preço aceito (em basis points, ex: 10500 = 105%)
    }

    event SwapExecuted(
        address indexed user,
        address indexed tokenIn,
        address indexed tokenOut,
        uint256 amountIn,
        uint256 amountOut,
        uint24 fee,
        uint256 timestamp
    );

    event MultiHopSwapExecuted(
        address indexed user,
        address indexed tokenIn,
        address indexed tokenOut,
        uint256 amountIn,
        uint256 amountOut,
        uint256 routeIndex,
        uint256 timestamp
    );

    event PriceCircuitBreakerTriggered(
        address indexed token,
        int256 currentPrice,
        int256 expectedPrice,
        string reason
    );

    event RouteCreated(uint256 indexed routeIndex, address[] tokens, uint24[] fees);

    // State
    ISwapRouter public swapRouter;
    uint256 public nextRouteId;

    mapping(uint256 => SwapRoute) public routes; // routeIndex -> route
    mapping(bytes32 => OracleConfig) public oracles; // keccak256(tokenAddress) -> oracle config
    mapping(address => uint256) public lastSwapTime; // Para rate limiting

    uint256 public constant MAX_SLIPPAGE_BPS = 500; // 5% slippage máximo padrão
    uint256 public constant BPS = 10000; // Basis points
    uint256 public constant CIRCUIT_BREAKER_THRESHOLD = 1000; // 10% de desvio máximo

    constructor(address _swapRouter) Ownable() {
        require(_swapRouter != address(0), "Invalid swap router");
        swapRouter = ISwapRouter(_swapRouter);
    }

    /**
     * @notice Registra um oracle Chainlink para um token
     * @param token Endereço do token
     * @param priceFeed Endereço do price feed
     * @param stalePriceThreshold Tempo máximo para dados considerados "frescos"
     * @param lowerBound Limite inferior em basis points (ex: 9500 = 95%)
     * @param upperBound Limite superior em basis points (ex: 10500 = 105%)
     */
    function registerOracle(
        address token,
        address priceFeed,
        uint256 stalePriceThreshold,
        int256 lowerBound,
        int256 upperBound
    ) external onlyOwner {
        require(token != address(0), "Invalid token");
        require(priceFeed != address(0), "Invalid price feed");
        require(stalePriceThreshold > 0, "Invalid threshold");
        require(lowerBound > 0 && upperBound > lowerBound, "Invalid bounds");

        uint8 decimals = AggregatorV3Interface(priceFeed).decimals();
        bytes32 tokenKey = keccak256(abi.encodePacked(token));

        oracles[tokenKey] = OracleConfig({
            priceFeed: priceFeed,
            decimals: decimals,
            stalePriceThreshold: stalePriceThreshold,
            lowerBoundPercent: lowerBound,
            upperBoundPercent: upperBound
        });
    }

    /**
     * @notice Cria uma rota de swap multi-hop
     * @param tokens Array de tokens na rota
     * @param fees Array de fees para cada hop
     * @return routeId ID da rota criada
     */
    function createRoute(
        address[] calldata tokens,
        uint24[] calldata fees
    ) external onlyOwner returns (uint256 routeId) {
        require(tokens.length >= 2, "Route must have at least 2 tokens");
        require(fees.length == tokens.length - 1, "Fee array length must be tokens.length - 1");

        // Validar tokens
        for (uint256 i = 0; i < tokens.length; i++) {
            require(tokens[i] != address(0), "Invalid token");
            if (i > 0) {
                require(tokens[i] != tokens[i - 1], "Duplicate tokens in route");
            }
        }

        routeId = nextRouteId;
        routes[routeId] = SwapRoute({
            tokens: tokens,
            fees: fees,
            active: true
        });

        emit RouteCreated(routeId, tokens, fees);
        nextRouteId++;
        return routeId;
    }

    /**
     * @notice Executa swap simples (single hop) via Uniswap V3
     * @param tokenIn Token de entrada
     * @param tokenOut Token de saída
     * @param fee Fee tier do pool
     * @param amountIn Quantidade a trocar
     * @param minAmountOut Proteção de slippage (mínimo a receber)
     * @return amountOut Quantidade recebida
     */
    function swapExactInputSingle(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn,
        uint256 minAmountOut
    ) external nonReentrant whenNotPaused returns (uint256 amountOut) {
        require(tokenIn != address(0) && tokenOut != address(0), "Invalid tokens");
        require(tokenIn != tokenOut, "Tokens must be different");
        require(amountIn > 0, "Amount must be > 0");

        // Validar preço se oracle registrado
        _validatePrice(tokenIn, tokenOut, amountIn, minAmountOut);

        // Transferir tokens
        IERC20(tokenIn).safeTransferFrom(msg.sender, address(this), amountIn);

        // Aprovar router
        IERC20(tokenIn).forceApprove(address(swapRouter), amountIn);

        // Executar swap
        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter.ExactInputSingleParams({
            tokenIn: tokenIn,
            tokenOut: tokenOut,
            fee: fee,
            recipient: msg.sender,
            deadline: block.timestamp + 60, // 1 minuto
            amountIn: amountIn,
            amountOutMinimum: minAmountOut,
            sqrtPriceLimitX96: 0 // Sem limite adicional (já temos minAmountOut)
        });

        amountOut = swapRouter.exactInputSingle(params);

        // Rate limiting
        lastSwapTime[msg.sender] = block.timestamp;

        emit SwapExecuted(msg.sender, tokenIn, tokenOut, amountIn, amountOut, fee, block.timestamp);
        return amountOut;
    }

    /**
     * @notice Executa swap multi-hop via rota predefinida
     * @param routeId ID da rota
     * @param amountIn Quantidade a trocar
     * @param minAmountOut Proteção de slippage
     * @return amountOut Quantidade recebida
     */
    function swapExactInputMultiHop(
        uint256 routeId,
        uint256 amountIn,
        uint256 minAmountOut
    ) external nonReentrant whenNotPaused returns (uint256 amountOut) {
        require(routeId < nextRouteId, "Route does not exist");
        SwapRoute storage route = routes[routeId];
        require(route.active, "Route is inactive");
        require(amountIn > 0, "Amount must be > 0");

        address tokenIn = route.tokens[0];
        address tokenOut = route.tokens[route.tokens.length - 1];

        // Validar preço
        _validatePrice(tokenIn, tokenOut, amountIn, minAmountOut);

        // Transferir tokens
        IERC20(tokenIn).safeTransferFrom(msg.sender, address(this), amountIn);

        // Aprovar router
        IERC20(tokenIn).forceApprove(address(swapRouter), amountIn);

        // Construir path (tokens entrelçados com fees)
        bytes memory path = _encodePath(route.tokens, route.fees);

        // Executar swap
        ISwapRouter.ExactInputParams memory params = ISwapRouter.ExactInputParams({
            path: path,
            recipient: msg.sender,
            deadline: block.timestamp + 60,
            amountIn: amountIn,
            amountOutMinimum: minAmountOut
        });

        amountOut = swapRouter.exactInput(params);

        // Rate limiting
        lastSwapTime[msg.sender] = block.timestamp;

        emit MultiHopSwapExecuted(
            msg.sender,
            tokenIn,
            tokenOut,
            amountIn,
            amountOut,
            routeId,
            block.timestamp
        );

        return amountOut;
    }

    /**
     * @notice Simula um swap sem executá-lo (para quote)
     * @param amountIn Quantidade
     * @param route Array de tokens
     * @return estimatedAmountOut Quantidade estimada de saída
     */
    function estimateSwap(
        uint256 amountIn,
        address[] calldata route
    ) external view returns (uint256 estimatedAmountOut) {
        require(route.length >= 2, "Invalid route");
        require(amountIn > 0, "Amount must be > 0");

        // Simplificado: seria necessário fazer chamadas estáticas ao pool
        // Para demo, retorna com base em oracle (se disponível)

        address tokenIn = route[0];
        address tokenOut = route[route.length - 1];

        // Se temos oracle para ambos, usar para estimar
        bytes32 inKey = keccak256(abi.encodePacked(tokenIn));
        bytes32 outKey = keccak256(abi.encodePacked(tokenOut));

        if (oracles[inKey].priceFeed != address(0) && oracles[outKey].priceFeed != address(0)) {
            int256 priceIn = _getPrice(tokenIn);
            int256 priceOut = _getPrice(tokenOut);

            if (priceIn > 0 && priceOut > 0) {
                estimatedAmountOut = (amountIn * uint256(priceIn)) / uint256(priceOut);
            }
        }

        return estimatedAmountOut;
    }

    // ===== INTERNAL FUNCTIONS =====

    /**
     * @notice Valida preço usando circuit breaker
     */
    function _validatePrice(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 minAmountOut
    ) internal view {
        bytes32 inKey = keccak256(abi.encodePacked(tokenIn));
        bytes32 outKey = keccak256(abi.encodePacked(tokenOut));

        if (oracles[inKey].priceFeed == address(0) || oracles[outKey].priceFeed == address(0)) {
            return; // Sem oracle, não validar
        }

        int256 priceIn = _getPrice(tokenIn);
        int256 priceOut = _getPrice(tokenOut);

        require(priceIn > 0 && priceOut > 0, "Invalid prices");

        // Calcular preço esperado
        uint256 expectedOut = (amountIn * uint256(priceIn)) / uint256(priceOut);

        // Circuit breaker: se desvio > 10%, rejeitar
        int256 deviation = int256(expectedOut) - int256(minAmountOut);
        int256 deviationPercent = (deviation * 10000) / int256(expectedOut);

        require(
            deviationPercent <= int256(CIRCUIT_BREAKER_THRESHOLD),
            "Price deviation too high"
        );
    }

    /**
     * @notice Obtém preço atual do Chainlink
     */
    function _getPrice(address token) internal view returns (int256) {
        bytes32 key = keccak256(abi.encodePacked(token));
        OracleConfig memory config = oracles[key];

        if (config.priceFeed == address(0)) return -1; // Oracle não registrado

        (
            ,
            int256 answer,
            ,
            uint256 updatedAt,

        ) = AggregatorV3Interface(config.priceFeed).latestRoundData();

        // Verificar se dados estão frescos
        require(
            block.timestamp - updatedAt <= config.stalePriceThreshold,
            "Stale price data"
        );

        require(answer > 0, "Invalid price");
        return answer;
    }

    /**
     * @notice Codifica path para Uniswap V3 (tokens + fees entrelçados)
     * Format: tokenA (20 bytes) + fee (3 bytes) + tokenB (20 bytes) + fee (3 bytes) + tokenC (20 bytes)
     */
    function _encodePath(
        address[] memory tokens,
        uint24[] memory fees
    ) internal pure returns (bytes memory) {
        bytes memory path = abi.encodePacked(tokens[0]);
        for (uint256 i = 0; i < fees.length; i++) {
            path = abi.encodePacked(path, fees[i], tokens[i + 1]);
        }
        return path;
    }

    // ===== EMERGENCY FUNCTIONS =====

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Recupera tokens presos no contrato (emergência)
     */
    function emergencyWithdraw(address token) external onlyOwner {
        uint256 balance = IERC20(token).balanceOf(address(this));
        require(balance > 0, "No balance");
        IERC20(token).safeTransfer(owner(), balance);
    }
}

